mapper.select:
SELECT order_id, user_id, product_id, quantity, order_no, amount, status, create_time, update_time
FROM t_order
WHERE order_id = #{orderId}

SELECT order_id, user_id, product_id, quantity, order_no, amount, status, create_time, update_time
FROM t_order
WHERE order_no = #{orderNo}

mapper.update:
UPDATE t_order
SET status = #{status}, update_time = #{updateTime}
WHERE order_id = #{orderId}
